import React, { useState } from "react";
import "./style.css";
export default function Login() {
    const [userInfo,setUserInfo]=useState({
        email:'',
        password:''
    })
    const handleonchange=(e)=>{
        const {name,value}=e.target
        setUserInfo((prestate)=>({
            ...prestate,
            [name]:value
        }))
    }

    const handlesignin=()=>{
        if(userInfo.email && userInfo.password){
            localStorage.setItem('email', userInfo.email);

        }
    }
  return (
    <div className="main--contaner">
      <section>
        <div className="left">
          <h1>Get Started</h1>
          <input type="email" placeholder="User name" onChange={handleonchange} />
          <input type="password" placeholder="Password" onChange={handleonchange}/>
          <button onClick={handlesignin}>Sign In</button>
        </div>
      </section>
      <section>
          <div className="right">
            <h1>Code Galaxy</h1>
            <img
              src="https://www.codegalaxy.co.in/uploads/2437f-logosmall.png"
              alt=""
            />
            <p>
              Lorem ipsum dolor sit amet consectetur, adipisicing elit. Maxime
              laudantium voluptatibus reprehenderit eius suscipit ab vitae
              tempora quos quaerat ipsam voluptas obcaecati nihil inventore
              sequi, architecto eos iure itaque! Consequuntur!
            </p>
          </div>
      </section>
    </div>
  );
}
