import React, { useEffect, useState } from "react";
import Layout from "../../../Layout/Layout";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { fetchUserData } from "./homeSlice";
import "./homestyle.css";
import LoginModal from "../../../components/LoginModal";
export default function Home() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [value, setValue] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage,setitemsPerPage]=useState(5)
  const [Open,setOpen]=useState(false)

  const { data, loading } = useSelector((state) => state.home);
  useEffect(() => {
    dispatch(fetchUserData(value));
  }, [value]);

  const totalItems = data?.length;
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // const startIndex = (currentPage - 1) * itemsPerPage;
  // const endIndex = startIndex + itemsPerPage;
  // const displayedItems = data?.slice(startIndex, endIndex);
  const handleopen=()=>{
    setOpen(true)
  }

  return (
    <div>
      <Layout setModal={setOpen}>
        <div className="p-4 overflow-x-scroll">
          <div className="search--courselist">
            <input
              type="text"
              onChange={(e) => setValue(e.target.value)}
              placeholder="search here..."
            />
          </div>
          <h2>Course List</h2>
          <div className="course--div">
            {data && data.length > 0 ? (
              data.map((item) => (
                <div
                  key={item.id}
                  className="course--card"
                  onClick={() =>
                    navigate("./detalis", { state: { userId: item.id } })
                  }
                >
                  <img
                    src={item?.thumbnail}
                    className="course--img"
                    alt="url"
                  />
                  <p>{item?.name}</p>
                  <p>{item?.description}</p>
                  <p>{item?.duration}</p>
                </div>
              ))
            ) : (
              <div className="error-message">loading...</div>
            )}
          </div>
        </div>
      </Layout>
      {Open && <LoginModal show={Open} setOpen={setOpen}/>}
    </div>
  );
}
