import React, { useState } from "react";
import Layout from "../../../Layout/Layout";
import "./style.css";
import { useDispatch } from "react-redux";
import { createAgents } from "../Home/homeSlice";
import LoginModal from "../../../components/LoginModal";
export default function CreateUser() {
  const dispatch = useDispatch();
  const [agent, setAgent] = useState([]);
  const [fullName, setFullName] = useState('');
  const [password, setpassword] = useState('');
  const [editIndex, setEditIndex] = useState(null);

  const handlesubmit = () => {
    if (agent.trim() === '') return;
    if (editIndex !== null) {
      const updatedTodos = [...agent];
      updatedTodos[editIndex] = { agent, password };
      setAgent(updatedTodos);
      setEditIndex(null);
    } else {
      setAgent([...agent, { agent, password }]);
    }
    setFullName('');
    setpassword('');
  };

  // const editTodo = (index) => {
  //   const { agent, password } = agent[index];
  //   setFullName(agent);
  //   setpassword(password);
  //   setEditIndex(index);
  // };

  // const deleteTodo = (index) => {
  //   const updatedTodos = agent.filter((_, i) => i !== index);
  //   setAgent(updatedTodos);
  // };
  return (
    <div>
      <Layout >
        <div className="create-user">
          <h2>Create Agents</h2>
          <div className="create">
            <input
              type="text"
              name="fullname"
              placeholder="Full Name"
              onChange={(e)=>setFullName(e.target.value)}
              value={fullName}
            />
            {/* <input
              type="email"
              name="email"
              placeholder="Enter email"
              onChange={(e)=>}
              value={user.email}
            />
            <input
              type="number"
              name="phone"
              id=""
              placeholder="Phone nm"
              onChange={handlechange}
              value={user.phone}
            /> */}
            <input
              type="password"
              name="password"
              id=""
              placeholder="Set Password"
              onChange={(e)=>e.target.value}
              value={password}
            />
            <button onClick={handlesubmit}>Create Agent</button>
          </div>
        </div>
        <div className="agentlist">
          <table>
            <tr>
              <th>Full Nmae</th>
              <th>Email Id</th>
              <th>Phone No</th>
              <th>Password</th>
              <th>Edit</th>
            </tr>
            <tr>
              {agent && agent.map((item) => (
                <div>
                  <td>{item.fullname}</td>
                  {/* <td>{item.email}</td>
                  <td>{item.phone}</td> */}
                  <td>{item.password}</td>
                  <td>
                    <span class="material-symbols-outlined">edit</span>
                  </td>
                </div>
              ))}
            </tr>
          </table>
        </div>
        {Open && <LoginModal show={Open} setOpen={setOpen} />}
      </Layout>
    </div>
  );
}
